import React from "react";
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Divider,
  Box,
  useTheme,
} from "@mui/material";
import {
  Dashboard as DashboardIcon,
  FitnessCenter as FitnessCenterIcon,
  People as PeopleIcon,
  AttachMoney as AttachMoneyIcon,
  Event as EventIcon,
  Settings as SettingsIcon,
  Assessment as AssessmentIcon,
} from "@mui/icons-material";
import { useTranslation } from "react-i18next";

const drawerWidth = 240;

function Sidebar({ open, onClose, onMenuSelect, currentPage }) {
  const { t } = useTranslation();
  const theme = useTheme();

  const menuItems = [
    { id: "dashboard", icon: <DashboardIcon />, text: t("sidebar.dashboard") },
    {
      id: "workouts",
      icon: <FitnessCenterIcon />,
      text: t("sidebar.workouts"),
    },
    { id: "students", icon: <PeopleIcon />, text: t("sidebar.students") },
    { id: "finances", icon: <AttachMoneyIcon />, text: t("sidebar.finances") },
    { id: "schedule", icon: <EventIcon />, text: t("sidebar.schedule") },
    { id: "reports", icon: <AssessmentIcon />, text: t("sidebar.reports") },
  ];

  const handleMenuItemClick = (pageId) => {
    onMenuSelect(pageId);
    if (onClose) onClose();
  };

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        [`& .MuiDrawer-paper`]: {
          width: drawerWidth,
          boxSizing: "border-box",
          bgcolor: theme.palette.background.paper,
        },
      }}
      open
    >
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: 2,
          borderBottom: `1px solid ${theme.palette.divider}`,
        }}
      >
        <FitnessCenterIcon sx={{ mr: 1, color: theme.palette.primary.main }} />
        <Box sx={{ typography: "h6", color: theme.palette.primary.main }}>
          {t("header.title")}
        </Box>
      </Box>

      <List>
        {menuItems.map((item) => (
          <ListItem key={item.id} disablePadding>
            <ListItemButton
              selected={currentPage === item.id}
              onClick={() => handleMenuItemClick(item.id)}
            >
              <ListItemIcon
                sx={{
                  color:
                    currentPage === item.id
                      ? theme.palette.primary.main
                      : theme.palette.text.secondary,
                }}
              >
                {item.icon}
              </ListItemIcon>
              <ListItemText
                primary={item.text}
                primaryTypographyProps={{
                  fontWeight: currentPage === item.id ? "bold" : "normal",
                  color:
                    currentPage === item.id
                      ? theme.palette.primary.main
                      : theme.palette.text.primary,
                }}
              />
            </ListItemButton>
          </ListItem>
        ))}
      </List>

      <Divider />

      <List>
        <ListItem disablePadding>
          <ListItemButton
            selected={currentPage === "settings"}
            onClick={() => handleMenuItemClick("settings")}
          >
            <ListItemIcon>
              <SettingsIcon />
            </ListItemIcon>
            <ListItemText primary={t("sidebar.settings")} />
          </ListItemButton>
        </ListItem>
      </List>
    </Drawer>
  );
}

export default Sidebar;
