import React, { useState, useEffect } from "react";
import { ThemeProvider, CssBaseline, Box } from "@mui/material";
import { lightTheme, darkTheme } from "./themes/theme";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Dashboard from "./components/Dashboard";
import WorkoutView from "./components/WorkoutView";
import StudentList from "./components/StudentList";
import StudentWorkoutList from "./components/StudentWorkoutList";
import WorkoutTemplates from "./components/WorkoutTemplates";
import "./i18n";
import { useTranslation } from "react-i18next";

function App() {
  // Estado para o tema
  const [theme, setTheme] = useState("light");

  // Estado para a navegação
  const [currentPage, setCurrentPage] = useState("dashboard");

  // Estado para o idioma
  const [language, setLanguage] = useState("pt");
  const { i18n } = useTranslation();

  // Estados para os dados da aplicação
  const [studentData, setStudentData] = useState({
    name: "",
    age: "",
    goals: "",
    frequency: "",
    sessions: "",
    date: null,
  });

  const [exercises, setExercises] = useState([]);
  const [observations, setObservations] = useState("");

  // Carregar preferência de tema do localStorage
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme");
    if (savedTheme) {
      setTheme(savedTheme);
    }

    const savedLanguage = localStorage.getItem("language");
    if (savedLanguage) {
      setLanguage(savedLanguage);
      i18n.changeLanguage(savedLanguage);
    }
  }, [i18n]);

  // Efeito para mudar o idioma quando o state mudar
  useEffect(() => {
    i18n.changeLanguage(language);
    localStorage.setItem("language", language);
  }, [language, i18n]);

  // Função para alternar entre temas claro e escuro
  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);
  };

  // Função para navegar entre as páginas
  const handlePageChange = (pageId) => {
    setCurrentPage(pageId);
  };

  // Renderizar o conteúdo com base na página atual
  const renderContent = () => {
    switch (currentPage) {
      case "dashboard":
        return <Dashboard />;
      case "workouts":
        return <StudentWorkoutList />;
      case "workout-templates":
        return <WorkoutTemplates />;
      case "students":
        return <StudentList />;
      case "student-form":
        return (
          <div>
            <Box sx={{ p: 3 }}>
              <h2>Formulário de Estudante</h2>
              <p>Esta funcionalidade será implementada em breve.</p>
            </Box>
          </div>
        );
      // Adicionar outras páginas conforme necessário
      default:
        return <Dashboard />;
    }
  };

  return (
    <ThemeProvider theme={theme === "light" ? lightTheme : darkTheme}>
      <CssBaseline />
      <Box sx={{ display: "flex" }}>
        {/* Menu Lateral */}
        <Sidebar
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          language={language}
          setLanguage={setLanguage}
        />

        {/* Conteúdo Principal */}
        <Box sx={{ flexGrow: 1, height: "100vh", overflow: "auto" }}>
          <Header toggleTheme={toggleTheme} currentTheme={theme} />

          {/* Área de conteúdo */}
          {renderContent()}
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
