import React, { useState, useEffect } from "react";
import { ThemeProvider, CssBaseline, Container, Box } from "@mui/material";
import { lightTheme, darkTheme } from "./themes/theme";
import Header from "./components/Header";
import StudentForm from "./components/StudentForm";
import ExerciseTable from "./components/ExerciseTable";
import Observations from "./components/Observations";
import "./i18n";

function App() {
  // Estado para o tema
  const [theme, setTheme] = useState("light");

  // Estados para os dados da aplicação
  const [studentData, setStudentData] = useState({
    name: "",
    age: "",
    goals: "",
    frequency: "",
    sessions: "",
    date: null,
  });

  const [exercises, setExercises] = useState([]);
  const [observations, setObservations] = useState("");

  // Carregar preferência de tema do localStorage
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme");
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  // Função para alternar entre temas claro e escuro
  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);
  };

  return (
    <ThemeProvider theme={theme === "light" ? lightTheme : darkTheme}>
      <CssBaseline />
      <Header toggleTheme={toggleTheme} currentTheme={theme} />

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Box sx={{ mb: 4 }}>
          {/* Formulário de informações do aluno */}
          <StudentForm
            studentData={studentData}
            setStudentData={setStudentData}
          />

          {/* Tabela de exercícios com abas para grupos musculares */}
          <ExerciseTable exercises={exercises} setExercises={setExercises} />

          {/* Seção de observações */}
          <Observations
            observations={observations}
            setObservations={setObservations}
          />
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
